import React from 'react';
import './App.css';
import UserDash from './components/UserDash'
import PrivateRoute from './utils/PrivateRoute'
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import {Logout} from './components/Logout'
import {axiosWithAuth} from '../utils/axiosWithAuth'
import {AuthContext} from'./Contexts/AuthContext' 



function App() {


  const recipeEdit = recipe => {
    axiosWithAuth()
    .put(`/auth/user/recipes${recipes.id}`, recipe)
    .then(res => {
        setRecipes(res.data)
    })
    .catch(err => {
        console.log(err)
    })
}
//post request to add post newPost

const addRecipe = newRecipe => {
    axiosWithAuth()
    .post('/auth/user/:id', newRecipe )
    .then(res => {
        setRecipes(res.data)
    })
    .catch(err => {
        console.log(err)
    })
}
//delete request for deletepost
const deleteRecipe = post => {
    axiosWithAuth()
    .delete(`/auth/user/recipes/${recipes.id}`)
    .then(res => {
        setRecipes(res.data)
    })
    .catch(err => {
        console.log(err)
    })
}

  return (

      <div className="App">
        <AuthContext.Provider value={{recipeEdit, addRecipe, deleteRecipe}}>
           <UserDash/>

        <Router> 
         <PrivateRoute exact path='/chefdash' component={ChefDash}/>
         <Route path='/login' component={Login}/> 
         <Route path='/userdash' component={UserDash}/>
        </Router> 
        </AuthContext.Provider>
      </div>


  );
}

export default App;
